angular.module("creayh",["ngAnimate"]).controller("mainController",["$scope","$http",function(a,b){a.getGitInfo=function(){a.userNotFound=!1,a.loaded=!1,b.get("https://api.github.com/users/"+a.username).success(function(b){a.user=b,a.loaded=!0}).error(function(){a.userNotFound=!0,a.loaded=!1}),b.get("https://api.github.com/users/"+a.username+"/repos").success(function(b){a.repos=b,a.reposFound=b.length>0})}}]);